		<script type="text/javascript">
			var nullflag = false;
			var interVal = null;
			$(function(){
				$("#username, #passwordHolder").keyup(function(event) {
					if (event.keyCode === 13) {
						$(".rtPanelSubmitBtnBg").click();
					}
				});
				announcer($('#errorMsgBox').text(),true);
				$('.rtPanelSubmitBtnBg').click(function(event){
					event.preventDefault(); //this will prevent the default submit
					var isOtpLogin = $("#getotp").is(':checked');
					var status = false;
					status = checkUserName(isOtpLogin);
					if(status){
							$('#signInForm').submit();
					}
				});
			});

			function submitForm(){
				var password = $("#passwordHolder");
				var realPassword = $("#password");
				var otp = $("#otp");
				var src = $("#src");
				realPassword.val(password.val());
				password.val('');
				var isOtpLogin = $("#getotp").is(':checked');
				if(!isOtpLogin){
					document.signInForm.action = "/metricstream/auth/basic?locale=en_US";
				}else{
					otp.val(realPassword.val())
					src.val("login")
					password.val('');
					realPassword.val('');
					document.signInForm.action = "/metricstream/auth/otp";
				}
			}
			function checkUserName(isOtpLogin){
				if(document.getElementById('errorMsgBox')){
					document.getElementById('errorMsgBox').hidden=true;
				}
				var userName = document.getElementById('username');
				if(userName){
					var status = false;
					if (userName.value === "" || userName.value.trim().length === 0) {
						applyCSS("username", true);
						$('#username').attr("aria-describedby","uname_null");
						$('#username').attr("aria-invalid","true");
						$("#uname_null").show();
						$("#forgotUname").css("margin-top","-10px");
						nullflag = true;
						$('#username').focus();
						checkPassword(isOtpLogin);
						status = false; // cancel submission
					}else if (userName.value || userName.value.length > 0) {
						applyCSS("username", false);
						userName.value = userName.value.trim();
						$('#username').removeAttr("aria-describedby","uname_null");
						$('#username').attr("aria-invalid","false");
						nullflag = false;
						status = checkPassword(isOtpLogin);
					}
					return status;
				}
			}

			function checkPassword(isOtpLogin) {
				var password = $("#passwordHolder");
				if(password){
					if (!password.val()) {
						applyCSS("password", true);
						if(!isOtpLogin){
							$('#passwordHolder').attr("aria-describedby","pwd_null");
							$('#passwordHolder').attr("aria-invalid","true");
							$("#pwd_null").show();
						}else{
							$('#passwordHolder').attr("aria-describedby","otp_null");
							$('#passwordHolder').attr("aria-invalid","true");
							$("#otp_null").show();
						}

						if(!nullflag){
							nullflag = true;
							$('#passwordHolder').focus();
						}
						return false; // cancel submission
					}else{
						applyCSS("password", false);
						$('#passwordHolder').removeAttr("aria-describedby","pwd_null");
						$('#passwordHolder').attr("aria-invalid","false");
						nullflag = false;
						return true;
					}
				}
			}
			function applyCSS(field, isError){
				var errorLable = document.getElementsByName(field+"ErrorLble");
				if(errorLable && errorLable[0]){
					errorLable[0].hidden = !isError;
				}
			}

			function announcer(message, annoucementEntirety){
							$('#announcerPlaceholder').html('<p>' + message + '</p>');
			                if(annoucementEntirety)
			                    $('#announcerPlaceholder').attr('aria-atomic', annoucementEntirety);

			}
			function getLocalizedData(){
				var selected_locale = "";
				if( document.getElementById('locale').hidden){
					selected_locale = document.getElementById('localeDevice').value;
				}else{
					selected_locale = document.getElementById('locale').value;
				}
				var url = window.location.href;
				var position = url.indexOf('?');
				if(position > 0){
					url = url.slice(0,position);
				}
				var action = document.getElementById('action').value;
				if(action !== 'null'){
					url  = url + '?'+'type='+action;
				}
				document.getElementById('locale').hidden = true;
				if(url.indexOf('?') > 0){
					url  = url + '&'+'locale='+selected_locale;
				}else{
					url  = url + '?'+'locale='+selected_locale;
				}
				window.location.href  = url;
			}

			function languageSelected(selected_locale){
				var ldapTypeStr = "type=ldap";
				var localeStr = "locale=";
				var url = window.location.href;
				if(url.indexOf(localeStr) !== -1){
					url = url.substring(0, url.indexOf(localeStr));
				}
				if(url.substring(0, url.indexOf(localeStr)) == "" && url.indexOf(ldapTypeStr) > 0){
					window.location.href  = url.substring(0, url.indexOf(ldapTypeStr)+ldapTypeStr.length) +'&'+localeStr+selected_locale;
				}else{
					window.location.href  = url.substring(0, url.indexOf('?'+localeStr)) + '?'+localeStr+selected_locale;
				}

			}
			function localekeyhandle(e){
				if(e.which !== 32 && e.which !== 9){
					e.preventDefault();
				}

			}

			function otpGeneration(){
				var uname = $("#username").val();
						if(uname ==="" || uname===null){
							$("#getotp").prop('checked', false);
							$("#uname_null").show();
							$("#forgotUname").css("margin-top","-10px");
							nullflag = true;
							$('#username').focus();
						}else{
							$("#uname_null").hide();
							$("#forgotPass").hide();
							$("#pwd").text("OTP");
							$("#sr-pwd").text("OTP");
							$.ajax({
								  type: "POST",
								  url: "/metricstream/auth/otp",
								  data: { username: $("#username").val(), src:"login"},
								  cache: false,
								  success: function(data){
								  },
								  complete:function(data){
										  startTimer(2, 'timer');
										  $('#otp_success').show();
										  $('#otp_success').focus();
										  announcer($('#otp_success').text(),true);
										  $('#passwordHolder').val('');
										  $("#pwd_null").hide();
										  $('#passwordHolder').focus();
								  },
								  error:function(error){
								  }
							});
						}
			}

			$(function(){
				$("#otp_success").hide();

				$("#username").on('change', function(event){
						document.getElementById("timer").innerHTML='';
						clearInterval(interVal);
						$("#otp_success").hide();
						$("#getotp").prop('checked', false);
						$("#forgotPass").show();
						$("#pwd").text("Password");
