					}
				});

				$(document.body).on('click','#getotplink', function(e) {
					otpGeneration();
				});

			});

			function startTimer(minutes, dom) {
				var duration = 60 * minutes;
				display = document.querySelector('#' + dom);
				var timer = duration, minutes, seconds;
				interVal = setInterval(function () {
								minutes = parseInt(timer / 60, 10);
								seconds = parseInt(timer % 60, 10);
								minutes = minutes < 10 ? "0" + minutes : minutes;
								seconds = seconds < 10 ? "0" + seconds : seconds;
								//display.textContent = minutes + ":" + seconds;
								document.getElementById(dom).innerHTML="<p>Please wait for " + minutes + "minutes " + seconds + "seconds to get resend OTP link.</p>";
								if (--timer < 0) {
									clearInterval(interVal);
									document.getElementById(dom).innerHTML="<p>Didn't receive OTP yet? <a id='getotplink'>Resend OTP</a></p>";
									$("#otp_success").hide();
								}
								}, 1000);
			}
